package objective;
import java.util.Scanner;

public class datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte bTemp = 121;			//1byte (numbers)
		short sTemp = 12312;		//2bytes (numbers)
		int iTemp = 777;			//4bytes (numbers)
		long lTemp = 12313223;		//8bytes (numbers)
		float fTemp = 1.123456789f;	//4bytes (fractional numbers)
		double dTemp = 1.123456789;	//8bytes (fractional numbers)
		boolean boolTemp = true;	//true or false values
		char cTemp = 'A';			//2bytes (one word/'')
		String stTemp = "ab";		// (one word and more/"")
		
		System.out.println(bTemp);
		System.out.println(sTemp);
		System.out.println(iTemp);
		System.out.println(lTemp);
		System.out.println(fTemp);
		System.out.println(dTemp);
		System.out.println(boolTemp);
		System.out.println(cTemp);
		System.out.println(stTemp);
	}

}
